INSERT INTO `movie`(`movie_id`, `name1`, `name2`, `director`, `screenwriter`, `actot`, `type`, `country`, `language`, `premiere`, `num`, `movie_length`, `introduction`, `category`) VALUES (1, '后翼弃兵', 'THE QUENE\'S GAMBIT', '斯科特·弗兰克', '艾伦·斯科特 / 斯科特·弗兰克', '安雅·泰勒-乔伊 / 哈利·米尔林 / 克洛伊·皮里 / 雅各布·福琼·劳埃德 / 玛丽埃尔·海勒 / 更多…', '剧情', '美国', '英语', '2020-10-23(美国)', 7, '50分钟', '　根据沃尔特·特维斯的小说改编，这个成长故事探索了天才背后真正的代价。20 世纪 50 年代末，年幼的贝丝·哈蒙（安雅·泰勒-乔伊饰）被遗弃并托付给肯塔基州的一家孤儿院。她具有惊人的国际象棋天赋，同时对政府提供给孩童的镇静剂上瘾。贝丝被自己心中的恶魔所困扰，在药物和痴迷的双重作用下，她变成了一个令人印象深刻、技艺高超且富有魅力的弃儿，并决心打破男性主导的国际象棋界建立的传统界限。', '欧美剧集');
INSERT INTO `movie`(`movie_id`, `name1`, `name2`, `director`, `screenwriter`, `actot`, `type`, `country`, `language`, `premiere`, `num`, `movie_length`, `introduction`, `category`) VALUES (2, '黑袍纠察队', 'THE BOYS', '菲利浦·斯格里西亚', '艾力克·克莱普科', '卡尔·厄本 / 吉姆·比弗 / 安东尼·斯塔尔 / 艾琳·莫里亚蒂 / 切斯·克劳福 / 更多…', '喜剧 / 动作 / 科幻 / 犯罪', '美国', '英语', '2020-09-04(美国)', 8, '60分钟', '剧中讲述一群被旁人称为「The Boys」的义警，他们专门对付因为自身能力而腐败﹑黑化的超级英雄。而他们的大目标就是金玉其外，败絮其中的英雄团队「The Seven」，以及管理团队﹑掩盖黑料的企业「Vought」。', '欧美剧集');
INSERT INTO `movie`(`movie_id`, `name1`, `name2`, `director`, `screenwriter`, `actot`, `type`, `country`, `language`, `premiere`, `num`, `movie_length`, `introduction`, `category`) VALUES (3, '王冠', 'THE CROWN', '本·卡隆 / 菲利普·马丁', '皮特·摩根', '克莱尔·芙伊 / 马特·史密斯 / 萨姆·斯皮格尔 / 凡妮莎·柯比 / 马修·古迪 / 更多…', '剧情', '美国', '英语', '2017-12-08(美国)', 10, '50分钟', '马特·史密斯和约翰·利斯高加盟Netflix剧集《王冠》(The Crown)，二人分别饰演菲利普亲王和丘吉尔。剧集剧本由《女王》编剧彼得·摩根创作，首播集由《时时刻刻》导演史蒂芬·戴德利执导，讲述伊丽莎白二世与丘吉尔在二战后，重塑英伦的故事。之前确定由克莱尔·福伊出演伊丽莎白二世。', '欧美剧集');
INSERT INTO `movie`(`movie_id`, `name1`, `name2`, `director`, `screenwriter`, `actot`, `type`, `country`, `language`, `premiere`, `num`, `movie_length`, `introduction`, `category`) VALUES (4, '35岁少女', '35歳の少女', '猪股隆一', '游川和彦', '柴崎幸 / 坂口健太郎 / 桥本爱 / 田中哲司 / 富田靖子 / 更多…', '剧情', '日本', '日语', '2020-10-10(日本)', 10, '48分钟', ' #35岁的少女#天真烂漫的十岁小姑娘因为事故昏迷了25年。醒来后35岁的外表，10岁的内心。不仅这个世界变得遥远，连家人也免得陌生。怀着世界温柔的美梦睡去，醒来却落入了一场新的噩梦。', '日剧');
INSERT INTO `movie`(`movie_id`, `name1`, `name2`, `director`, `screenwriter`, `actot`, `type`, `country`, `language`, `premiere`, `num`, `movie_length`, `introduction`, `category`) VALUES (5, '#远程恋爱', 'リモラブ 〜普通の恋は邪道〜', '中岛悟 / 丸谷俊平 / 铃木勇马', '水桥文美江', '波瑠 / 松下洸平 / 间宫祥太朗 / 渡边大 / 高桥优斗 / 更多…', '喜剧', '日本', '日语', '2020-10-14(日本)', 8, '56分钟', '新冠肺炎袭来，特殊时期人人自危，而作为企业医生的大樱美美#波瑠#更是身负重担。为了保证全公司1000多人的健康，美美严格监控着每位员工的健康状况。某晚，美美在玩游戏的时候偶然和附近的玩家聊了起来，而美美却渐渐迷上了这种有一句没一句的对话。某一次的对话中，美美突然发现聊天的对象竟然是同一公司的某个人…', '日剧');
INSERT INTO `movie`(`movie_id`, `name1`, `name2`, `director`, `screenwriter`, `actot`, `type`, `country`, `language`, `premiere`, `num`, `movie_length`, `introduction`, `category`) VALUES (6, '钱断情始', 'おカネの切れ目が恋のはじまり', '土井裕泰 / 平野俊一', '大岛里美\r\n大岛里美\r\n', '松冈茉优 / 三浦春马 / 三浦翔平 / 北村匠海', '喜剧 / 爱情', '日本', '日语', '2020-09-15(日本)', 4, '123分钟', '松冈茉优饰演在公司会计部工作的30岁左右女子九鬼玲子，她以“清贫”为口号而生活。有一天，中坚玩具制造商的名门子弟猿渡庆太因为过度浪费而从营业部被调到了会计部，玲子无意中被任命为庆太的指导人员。以此为契机，“清贫女子”和“浪费男子”的夏日故事开始了。\r\n此外，三浦翔平饰演玲子的初恋对象、拥有公认会计师资格的早乙女健，北村匠海饰演猿渡庆太的后辈、“吝啬节约男子”板垣纯。', '日剧');
INSERT INTO `movie`(`movie_id`, `name1`, `name2`, `director`, `screenwriter`, `actot`, `type`, `country`, `language`, `premiere`, `num`, `movie_length`, `introduction`, `category`) VALUES (7, '七号房的礼物', 'Gift From Room', '李焕庆', '李焕庆 / 刘英雅', '柳承龙 / 朴信惠 / 郑镇荣 / 金正泰 / 吴达洙/ 更多…', '剧情 / 喜剧 / 家庭', '韩国', '韩语', '2013-01-23(韩国)', NULL, '127分钟', '1997年，只有6岁儿童智商的智障男子李龙久（柳成龙 饰）和可爱的女儿艺胜（葛素媛 饰）相依为命，生活虽然简单清贫，却充满幸福。某天，执着为女儿买美少女战士书包的龙久意外卷入一起幼童诱拐奸杀案，而死者竟是警察局长的女儿。龙久懵懂无知，搞不清状况，昏头昏脑就被投入监狱。在7号牢房中，聚集着走私犯蘇杨浩（吴达洙 饰）、诈骗犯崔春浩（朴元尚 饰）、通奸犯姜万范（金正泰 饰）、恐吓犯老徐（金基灿 饰）和抢劫犯申奉植（郑万植 饰）等五毒俱全的“社会渣滓”。龙久孩子般纯洁的心渐渐感动了这几个“大坏蛋”，他们甚至不惜冒险将艺胜带入牢房与父亲相会。', '韩剧');
INSERT INTO `movie`(`movie_id`, `name1`, `name2`, `director`, `screenwriter`, `actot`, `type`, `country`, `language`, `premiere`, `num`, `movie_length`, `introduction`, `category`) VALUES (8, '王国', 'Kingdom', '金成勋', '金恩熙', '朱智勋 / 裴斗娜 / 柳承龙 / 金相浩 / 许峻豪 / 更多…', '剧情 / 动作 / 惊悚', '韩国 / 美国', '韩语', '2019-01-25(韩国)', 6, '50分钟', '在一个充斥着贪腐和饥荒的王国中，关于国王死亡的神秘传闻沸沸扬扬，同时一场奇怪的瘟疫传播开来，感染者可获得不死之身并嗜食人肉。成为阴谋牺牲品的皇太子开始踏上揭露邪恶阴谋，拯救子民的征程。', '韩剧');
INSERT INTO `movie`(`movie_id`, `name1`, `name2`, `director`, `screenwriter`, `actot`, `type`, `country`, `language`, `premiere`, `num`, `movie_length`, `introduction`, `category`) VALUES (9, '寄生虫', 'Parasite', '奉俊昊', '奉俊昊', '宋康昊 / 李善均 / 赵汝贞 / 崔宇植 / 朴素丹', '剧情', '韩国', '韩语', '2019-05-21(戛纳电影节)', NULL, '132分钟', '《寄生虫》讲述了发生在身份地位悬殊的两个家庭身上的故事：宋康昊饰演的无业游民父亲基泽，让寄托了家人生计希望的大儿子（崔宇植 饰）前往IT公司老总朴社长（李善均 饰）家应聘课外教师，随之发生了一连串意外事件。', '韩剧');
INSERT INTO `movie`(`movie_id`, `name1`, `name2`, `director`, `screenwriter`, `actot`, `type`, `country`, `language`, `premiere`, `num`, `movie_length`, `introduction`, `category`) VALUES (10, '真实的凡尔赛', 'The.Real.Versailles', 'Rosie Schellenberg', '', 'Helen Castor / Lucy Worsley / James Daybell / Mark Wallis', '纪录片 / 历史', '英国', '英语', '2016-05-30 (英国)', NULL, '54分钟', '在电视剧《凡尔赛宫》热播之际，主持人、历史学家Lucy Worsley和Helen Castor来到取景地，法国凡尔赛宫，讲述这座世界最伟大建筑之一背后的真实故事。', '纪录片/综艺');
INSERT INTO `movie`(`movie_id`, `name1`, `name2`, `director`, `screenwriter`, `actot`, `type`, `country`, `language`, `premiere`, `num`, `movie_length`, `introduction`, `category`) VALUES (11, '信条', 'Tenet', '克里斯托弗·诺兰', '克里斯托弗·诺兰', '约翰·大卫·华盛顿 / 罗伯特·帕丁森 / 伊丽莎白·德比茨基 / 肯尼思·布拉纳 / 亚伦·泰勒-约翰逊 / 等..', '剧情 / 动作 / 科幻', '美国 / 英国', '英语 / 俄语 / 乌克兰语 / 爱沙尼亚语 / 印地语 / 挪威语', '2020-08-26(英国)', NULL, '150分钟 / 144分钟(中国大陆)', '世界存亡危在旦夕，“信条”一词是唯一的线索与武器。主人公穿梭于全球各地，开展特工活动，力求揭示“信条”之谜，并完成一项超越了真实时间的神秘任务。这项任务并非时间之旅，而是【时空逆转】。', '欧美电影');
